using System.Collections;using System.Collections.Generic;using UnityEngine;public class NextLevel : MonoBehaviour {    void OnEnable()    {        GameManager.OnSuccess += MakeVisible;    }    void OnDisable()    {        GameManager.OnSuccess -= MakeVisible;    }    void MakeVisible()    {        this.gameObject.GetComponent<CanvasGroup>().alpha = 1;        this.gameObject.GetComponent<CanvasGroup>().interactable = true;        this.gameObject.GetComponent<CanvasGroup>().blocksRaycasts = true;    }}