using System.Collections;using System.Collections.Generic;using UnityEngine;public class SuccessParticles : MonoBehaviour {    void OnEnable()    {        GameManager.OnSuccess += SuccessEffects;    }    void OnDisable()    {        GameManager.OnSuccess -= SuccessEffects;    }    void SuccessEffects()    {        this.gameObject.GetComponent<ParticleSystem>().Play();    }}